/**
 * generate-icons-v2.js
 * AgendAI - Premium icon set generator \(v2\)
 * Design: gradient background, calendar grid, AI badge, no text clutter
 */
const sharp = require\("sharp"\);
const path  = require\("path"\);
const fs    = require\("fs"\);

const ASSETS = path.join\(__dirname, "mobile", "assets"\);

/* ── tiny SVG helpers ───────────────────────────────────────────── */
function q\(v\)          { return '"' + v + '"'; }
function attr\(k, v\)    { return " " + k + "=" + q\(v\); }
function tag\(name, attrs, children\) {
  var a = Object.keys\(attrs\).map\(function\(k\){ return attr\(k, attrs[k]\); }\).join\(""\);
  if \(children === undefined\) return "<" + name + a + "/>";
  return "<" + name + a + ">" + \(children || ""\) + "</" + name + ">";
}

/* ── main icon SVG ──────────────────────────────────────────────── */
/*
 * Parameters
 *   size          canvas size in px
 *   bgRadius      corner radius for background rect \(0 = no clip, for adaptive\)
 *   contentScale  fraction of canvas the calendar fills; use 0.72 for adaptive safe-area
 *   transparentBg true → white background \(for splash-icon export, unused here\)
 */
function buildIconSVG\(size, bgRadius, contentScale\) {
  var S  = size;
  var sc = contentScale;          // 0–1, fraction of S the calendar occupies
  var cx = S / 2;                 // canvas centre x
  var cy = S / 2;                 // canvas centre y

  /* ── calendar dimensions ── */
  var calW  = Math.round\(S * sc * 0.78\);   // width  ~60 % of canvas
  var calH  = Math.round\(calW * 1.05\);     // slightly taller than wide
  var calR  = Math.round\(calW * 0.10\);     // corner radius of calendar body
  var calX  = Math.round\(cx - calW / 2\);
  // shift calendar slightly above centre
  var calY  = Math.round\(cy - calH / 2 - S * 0.025\);

  /* ── header strip \(top ~22 % of calendar\) ── */
  var hdrH  = Math.round\(calH * 0.22\);

  /* ── pegs \(rings\) sticking up from top of calendar ── */
  var pegW  = Math.round\(S * 0.028\);
  var pegH  = Math.round\(S * 0.065\);
  var pegR  = Math.round\(pegW / 2\);
  // positioned at 25 % and 75 % across the calendar width
  var peg1X = Math.round\(calX + calW * 0.25 - pegW / 2\);
  var peg2X = Math.round\(calX + calW * 0.75 - pegW / 2\);
  var pegY  = Math.round\(calY - pegH * 0.55\);  // peg starts above the calendar top

  /* ── calendar grid \(days\) ── */
  // 3 rows × 4 columns of small rounded squares
  var cols   = 4, rows = 3;
  var bodyTop = calY + hdrH;
  var bodyH   = calH - hdrH;
  var padH    = Math.round\(bodyH * 0.11\);
  var padV    = Math.round\(bodyH * 0.10\);
  var gapH    = Math.round\(bodyH * 0.038\);
  var gapV    = Math.round\(bodyH * 0.038\);
  var totalInnerW = calW  - padH * 2;
  var totalInnerH = bodyH - padV * 2;
  var cellW = Math.round\(\(totalInnerW - gapH * \(cols - 1\)\) / cols\);
  var cellH = Math.round\(\(totalInnerH - gapV * \(rows - 1\)\) / rows\);
  var cellR = Math.round\(cellW * 0.28\);

  var cells = "";
  for \(var row = 0; row < rows; row++\) {
    for \(var col = 0; col < cols; col++\) {
      var cx2 = calX + padH + col * \(cellW + gapH\);
      var cy2 = bodyTop + padV + row * \(cellH + gapV\);
      // highlight: row 0 col 1  \(second cell, top row — "selected day"\)
      var isHighlight = \(row === 0 && col === 1\);
      // subtle accent: row 1 col 3  \(last of second row\)
      var isAccent    = \(row === 1 && col === 3\);
      var fill = isHighlight ? "#2563EB"
               : isAccent    ? "#60A5FA"
               :               "#BFDBFE";
      cells += tag\("rect", {
        x: cx2, y: cy2,
        width: cellW, height: cellH,
        rx: cellR, ry: cellR,
        fill: fill
      }\);
      // small white dot on the highlighted cell
      if \(isHighlight\) {
        cells += tag\("circle", {
          cx: Math.round\(cx2 + cellW / 2\),
          cy: Math.round\(cy2 + cellH / 2\),
          r:  Math.round\(cellW * 0.18\),
          fill: "rgba\(255,255,255,0.90\)"
        }\);
      }
    }
  }

  /* ── AI badge \(pill, bottom-right of calendar body\) ── */
  var badgeW  = Math.round\(calW  * 0.26\);
  var badgeH  = Math.round\(badgeW * 0.46\);
  var badgeR  = Math.round\(badgeH / 2\);
  var badgeX  = Math.round\(calX + calW - badgeW - calW * 0.055\);
  var badgeY  = Math.round\(bodyTop + bodyH - badgeH - bodyH * 0.07\);
  var aiFS    = Math.round\(badgeH * 0.56\);
  var aiBadge = tag\("rect", {
    x: badgeX, y: badgeY,
    width: badgeW, height: badgeH,
    rx: badgeR, ry: badgeR,
    fill: "#1E3A8A",
    opacity: "0.92"
  }\);
  var aiText  = tag\("text", {
    x: Math.round\(badgeX + badgeW / 2\),
    y: Math.round\(badgeY + badgeH * 0.73\),
    "text-anchor": "middle",
    "font-family": "Arial Black, Arial, sans-serif",
    "font-weight": "900",
    "font-size":   aiFS,
    "letter-spacing": Math.round\(aiFS * 0.06\),
    fill: "white"
  }, "AI"\);

  /* ── subtle sparkle in top-right area ── */
  // four thin diamond lines crossing at a point
  var spX = Math.round\(calX + calW + S * 0.025\);
  var spY = Math.round\(calY - S * 0.01\);
  var spR = Math.round\(S * 0.028\);  // "radius" of sparkle arms
  function sparkleArm\(dx, dy\) {
    return tag\("line", {
      x1: spX, y1: spY,
      x2: Math.round\(spX + dx\), y2: Math.round\(spY + dy\),
      stroke: "rgba\(255,255,255,0.75\)",
      "stroke-width": Math.round\(S * 0.006\),
      "stroke-linecap": "round"
    }\);
  }
  var sparkle = sparkleArm\(-spR, -spR\)   // top-left
              + sparkleArm\( spR,  spR\)   // bottom-right
              + sparkleArm\( spR, -spR\)   // top-right
              + sparkleArm\(-spR,  spR\)   // bottom-left
              + sparkleArm\(0, -spR * 1.4\)// up
              + sparkleArm\(0,  spR * 1.4\)// down
              + sparkleArm\(-spR * 1.4, 0\)// left
              + sparkleArm\( spR * 1.4, 0\);// right
  var sparkleCenter = tag\("circle", { cx: spX, cy: spY, r: Math.round\(S * 0.008\), fill: "white" }\);

  /* ── definitions: gradient + clipPath ── */
  var grad = tag\("linearGradient",
    { id: "bg", x1: "0%", y1: "0%", x2: "100%", y2: "100%",
      gradientUnits: "objectBoundingBox" },
    tag\("stop", { offset: "0%",   "stop-color": "#1E3A8A" }\) +
    tag\("stop", { offset: "100%", "stop-color": "#3B82F6" }\)
  \);
  // inner glow / highlight: semi-transparent white ellipse top-left
  var glowGrad = tag\("radialGradient",
    { id: "glow", cx: "28%", cy: "22%", r: "50%", gradientUnits: "objectBoundingBox" },
    tag\("stop", { offset: "0%",   "stop-color": "rgba\(255,255,255,0.18\)" }\) +
    tag\("stop", { offset: "100%", "stop-color": "rgba\(255,255,255,0.00\)" }\)
  \);
  var clipRect = tag\("rect", { x: 0, y: 0, width: S, height: S, rx: bgRadius, ry: bgRadius }\);
  var clipDef  = tag\("clipPath", { id: "bgClip" }, clipRect\);
  // clip for calendar corners
  var calClipRect = tag\("rect", { x: calX, y: calY, width: calW, height: calH, rx: calR, ry: calR }\);
  var calClipDef  = tag\("clipPath", { id: "calClip" }, calClipRect\);

  var defs = tag\("defs", {}, grad + glowGrad + clipDef + calClipDef\);

  /* ── assemble layers ── */
  // background
  var bgRect  = tag\("rect", { x: 0, y: 0, width: S, height: S,
                               rx: bgRadius, ry: bgRadius, fill: "url\(#bg\)" }\);
  // glow overlay
  var glowRect = tag\("rect", { x: 0, y: 0, width: S, height: S,
                                rx: bgRadius, ry: bgRadius, fill: "url\(#glow\)" }\);
  // calendar white body
  var calBody = tag\("rect", { x: calX, y: calY, width: calW, height: calH,
                               rx: calR, ry: calR,
                               fill: "white",
                               filter: "drop-shadow\(0 " + Math.round\(S*0.008\) + "px " + Math.round\(S*0.025\) + "px rgba\(0,0,0,0.22\)\)" }\);
  // dark blue header \(clipped to calendar corners\)
  var hdrBar  = tag\("rect", { x: calX, y: calY, width: calW, height: hdrH,
                               fill: "#1D4ED8",
                               "clip-path": "url\(#calClip\)" }\);
  // header highlight lines \(subtle white stripes in header\)
  var hl1 = tag\("rect", {
    x: Math.round\(calX + calW * 0.14\), y: Math.round\(calY + hdrH * 0.30\),
    width: Math.round\(calW * 0.35\), height: Math.round\(hdrH * 0.13\),
    rx: Math.round\(hdrH * 0.065\), fill: "rgba\(255,255,255,0.50\)"
  }\);
  var hl2 = tag\("rect", {
    x: Math.round\(calX + calW * 0.14\), y: Math.round\(calY + hdrH * 0.58\),
    width: Math.round\(calW * 0.22\), height: Math.round\(hdrH * 0.10\),
    rx: Math.round\(hdrH * 0.05\), fill: "rgba\(255,255,255,0.30\)"
  }\);
  // pegs
  var peg1 = tag\("rect", { x: peg1X, y: pegY, width: pegW, height: pegH,
                             rx: pegR, ry: pegR, fill: "#93C5FD" }\);
  var peg2 = tag\("rect", { x: peg2X, y: pegY, width: pegW, height: pegH,
                             rx: pegR, ry: pegR, fill: "#93C5FD" }\);

  var svg = defs
    + bgRect
    + glowRect
    + sparkle + sparkleCenter
    + calBody
    + hdrBar + hl1 + hl2
    + peg1 + peg2
    + cells
    + aiBadge + aiText;

  var viewBox = "0 0 " + S + " " + S;
  return '<?xml version="1.0" encoding="UTF-8"?>'
    + tag\("svg", { width: S, height: S, viewBox: viewBox, xmlns: "http://www.w3.org/2000/svg" }, svg\);
}

/* ── favicon SVG \(64 × 64, simplified\) ─────────────────────────── */
function buildFaviconSVG\(size\) {
  var S  = size;
  var bgR = Math.round\(S * 0.20\);

  /* calendar */
  var calX = Math.round\(S * 0.10\);
  var calY = Math.round\(S * 0.18\);
  var calW = Math.round\(S * 0.80\);
  var calH = Math.round\(S * 0.70\);
  var calR = Math.round\(S * 0.10\);
  var hdrH = Math.round\(calH * 0.28\);

  /* pegs */
  var pegW = Math.round\(S * 0.09\);
  var pegH = Math.round\(S * 0.16\);
  var pegR = Math.round\(pegW / 2\);
  var pegY = Math.round\(calY - pegH * 0.5\);

  /* 2x3 grid cells */
  var bodyTop = calY + hdrH;
  var bodyH   = calH - hdrH;
  var cols = 3, rows = 2;
  var padH = Math.round\(calW  * 0.10\);
  var padV = Math.round\(bodyH * 0.12\);
  var gapH = Math.round\(calW  * 0.04\);
  var gapV = Math.round\(bodyH * 0.08\);
  var cellW = Math.round\(\(calW - padH * 2 - gapH * \(cols - 1\)\) / cols\);
  var cellH = Math.round\(\(bodyH - padV * 2 - gapV * \(rows - 1\)\) / rows\);
  var cellR = Math.round\(cellW * 0.28\);

  var cells = "";
  for \(var row = 0; row < rows; row++\) {
    for \(var col = 0; col < cols; col++\) {
      var cx2 = calX + padH + col * \(cellW + gapH\);
      var cy2 = bodyTop + padV + row * \(cellH + gapV\);
      var isHL = \(row === 0 && col === 1\);
      cells += tag\("rect", {
        x: cx2, y: cy2, width: cellW, height: cellH,
        rx: cellR, ry: cellR,
        fill: isHL ? "#2563EB" : "#BFDBFE"
      }\);
    }
  }

  var grad = tag\("linearGradient",
    { id: "bg", x1: "0%", y1: "0%", x2: "100%", y2: "100%", gradientUnits: "objectBoundingBox" },
    tag\("stop", { offset: "0%",   "stop-color": "#1E3A8A" }\) +
    tag\("stop", { offset: "100%", "stop-color": "#3B82F6" }\)
  \);
  var clipRect = tag\("rect", { x: 0, y: 0, width: S, height: S, rx: bgR, ry: bgR }\);
  var clipDef  = tag\("clipPath", { id: "bgClip" }, clipRect\);
  var calClipRect = tag\("rect", { x: calX, y: calY, width: calW, height: calH, rx: calR, ry: calR }\);
  var calClipDef  = tag\("clipPath", { id: "calClip" }, calClipRect\);
  var defs = tag\("defs", {}, grad + clipDef + calClipDef\);

  var bg      = tag\("rect", { x: 0, y: 0, width: S, height: S, rx: bgR, ry: bgR, fill: "url\(#bg\)" }\);
  var calBody = tag\("rect", { x: calX, y: calY, width: calW, height: calH, rx: calR, ry: calR, fill: "white" }\);
  var hdr     = tag\("rect", { x: calX, y: calY, width: calW, height: hdrH, fill: "#1D4ED8", "clip-path": "url\(#calClip\)" }\);
  var peg1    = tag\("rect", { x: Math.round\(calX + calW*0.22\), y: pegY, width: pegW, height: pegH, rx: pegR, ry: pegR, fill: "#93C5FD" }\);
  var peg2    = tag\("rect", { x: Math.round\(calX + calW*0.69\), y: pegY, width: pegW, height: pegH, rx: pegR, ry: pegR, fill: "#93C5FD" }\);

  var inner = defs + bg + calBody + hdr + peg1 + peg2 + cells;
  return '<?xml version="1.0" encoding="UTF-8"?>'
    + tag\("svg", { width: S, height: S, viewBox: "0 0 " + S + " " + S, xmlns: "http://www.w3.org/2000/svg" }, inner\);
}

/* ── generate all files ─────────────────────────────────────────── */
async function generate\(\) {
  var tasks = [
    // icon.png — rounded corners, full canvas
    {
      name: "icon.png",
      svg:  buildIconSVG\(1024, 220, 1.0\),
      w: 1024, h: 1024
    },
    // adaptive-icon.png — NO rounded corners \(Android clips itself\),
    // content scaled to 72% to stay inside safe area
    {
      name: "adaptive-icon.png",
      svg:  buildIconSVG\(1024, 0, 0.88\),
      w: 1024, h: 1024
    },
    // favicon.png — 64×64 simplified
    {
      name: "favicon.png",
      svg:  buildFaviconSVG\(64\),
      w: 64, h: 64
    }
    // splash-icon.png is intentionally skipped \(kept as-is per instructions\)
  ];

  for \(var i = 0; i < tasks.length; i++\) {
    var task = tasks[i];
    var svgBuf = Buffer.from\(task.svg, "utf8"\);
    var outPath = path.join\(ASSETS, task.name\);
    await sharp\(svgBuf, { density: 144 }\)
      .resize\(task.w, task.h\)
      .png\({ compressionLevel: 9 }\)
      .toFile\(outPath\);
    var stat = fs.statSync\(outPath\);
    console.log\("[OK] " + task.name + "  \(" + stat.size + " bytes\)  -> " + outPath\);
  }
}

console.log\("AgendAI icon generator v2 — generating premium icons..."\);
generate\(\)
  .then\(function\(\) { console.log\("\\nAll done. splash-icon.png was intentionally left unchanged."\); }\)
  .catch\(function\(err\) { console.error\("\\nFAILED:", err.message || err\); process.exit\(1\); }\);
SCRIPT_EOF)
